mapper.select:
/* JgCmtTwo.selectContents */
SELECT JG_SEQ jgSeq , JG_GROUP_SEQ jgGroupSeq , JG_TITLE jgTitle , JG_CONT jgCont , JG_REPLY_USE_YN jgReplyUseYn , DATE_FORMAT(JG_RGST_DT,'%Y.%m.%d') jgRgstDt , F_USER_NM(JG_RGST_ID) jgRgstId , JG_ATCH_FILE_ID jgAtchFileId , JG_RE_ATCH_FILE_ID jgReAtchFileId
FROM t_jgcmttwo WHERE JG_USE_YN = 'Y' AND JG_SEQ = #{jgSeq}

/* JgCmtTwo.selectList */
SELECT JG_SEQ jgSeq , JG_GROUP_SEQ jgGroupSeq , JG_TITLE jgTitle , JG_CONT jgCont , JG_REPLY_USE_YN jgReplyUseYn , DATE_FORMAT(JG_RGST_DT,'%Y.%m.%d') jgRgstDt , F_USER_NM(JG_RGST_ID) jgRgstId , JG_ATCH_FILE_ID jgAtchFileId , JG_RE_ATCH_FILE_ID jgReAtchFileId
FROM t_jgcmttwo WHERE JG_USE_YN='Y' AND JG_SEQ = JG_GROUP_SEQ
ORDER BY JG_SEQ DESC LIMIT #{firstIndex} , #{recordCountPerPage}

mapper.insert:
		/* JgCmtTwo.insertContents */
		
			INSERT INTO t_jgcmttwo(
           	  	  JG_SEQ
           	  	, JG_GROUP_SEQ 
				, JG_TITLE 
				, JG_CONT
				, JG_RGST_ID 
				, JG_ATCH_FILE_ID 
			)
			VALUES (
				 (SELECT IFNULL(MAX(TC.JG_SEQ)+1,1) FROM t_jgcmttwo TC)
				,(SELECT IFNULL(MAX(TC.JG_SEQ)+1,1) FROM t_jgcmttwo TC)
				,#{jgTitle}
				,#{jgCont}
				,#{loginSeq}
				,#{jgAtchFileId}
			)

mapper.update:
		/* JgCmtTwo.updateContents */
		
			UPDATE t_jgcmttwo     
			 SET 
				  JG_TITLE    						= #{jgTitle }
				, JG_CONT							= #{jgCont }
				, JG_REPLY_USE_YN					= #{jgReplyUseYn }
				, JG_RVSE_DT 					 	= NOW()
				, JG_ATCH_FILE_ID 					= #{jgAtchFileId }
					
			WHERE  JG_SEQ =	#{jgSeq}

	/* JgCmtTwo.deleteContents */
	
		UPDATE t_jgcmttwo 
	       SET
          	 JG_USE_YN='N'
		WHERE JG_SEQ = #{jgSeq}

mapper.select:
/* JgCmtTwo.selectComments */
SELECT JG_SEQ jgSeq , JG_GROUP_SEQ jgGroupSeq , JG_TITLE jgTitle , JG_CONT jgCont , JG_REPLY_USE_YN jgReplyUseYn , DATE_FORMAT(JG_RGST_DT,'%Y.%m.%d') jgRgstDt , F_USER_NM(JG_RGST_ID) jgRgstId , JG_ATCH_FILE_ID jgAtchFileId , JG_RE_ATCH_FILE_ID jgReAtchFileId
FROM t_jgcmttwo WHERE JG_USE_YN = 'Y' AND JG_GROUP_SEQ = #{jgSeq} AND JG_SEQ <> JG_GROUP_SEQ

mapper.insert:
		/* JgCmtTwo.insertComments */
		
			INSERT INTO t_jgcmttwo(
				  JG_SEQ
           	  	, JG_GROUP_SEQ 
				, JG_CONT 
				, JG_REPLY_USE_YN					
				, JG_RGST_ID
				, JG_RE_ATCH_FILE_ID 
			)
			VALUES (
				(SELECT IFNULL(MAX(TC.JG_SEQ)+1,1) FROM t_jgcmttwo TC)
				,#{jgSeq}
				,#{jgCont}
				, "Y"
				,#{loginSeq}
				,#{jgReAtchFileId}
			)

mapper.update:
/* JgCmtTwo.updateComments */

	UPDATE t_jgcmttwo     
	 SET 
		  JG_CONT    										 = #{jgCont } 
		, JG_RVSE_DT 											 =  NOW()
		, JG_RE_ATCH_FILE_ID							 = #{jgReAtchFileId }
			
	WHERE   JG_SEQ <> JG_GROUP_SEQ
